pln_glc_04_02 = "護送車を見つけにいけ。",--Go find those transports.
pln_glc_04_03 = "護送車に向かえ。",--Get to the prison transports.
pln_glc_04_04 = "護送車を見つけろ。",--Find the prison transports.
pln_glc_04_05 = "行け! 護送車を発見しろ。",--Go go, find the prison transports.
pln_glc_04_06 = "トラックを見つけにいけ。",--Go find those trucks.
pln_glc_04_07 = "トラックを見つけろ。",--Find the prison trucks.
pln_glc_04_08 = "トラックに向かえ。",--Get to the trucks.
