hud_heist_kenaz_c_14_hl = "花火を取れ",--Take the fireworks
hud_heist_kenaz_c_15 = "花火を指定された位置に置け。",--Place the fireworks at the indicated spot.
hud_heist_kenaz_c_15_hl = "花火を置け",--Place the fireworks
