pln_watchdogs_stage1_58_any_01 = "",--You heard him - one minute, stay alive and get ready to lift the coke out of there!
pln_watchdogs_stage1_59_any_01 = "",--He is almost here, 30 seconds!
pln_watchdogs_stage1_60_any_01 = "",--Get ready!
pln_watchdogs_stage1_61_any_01 = "",--Alright, there he is - move it!
